my_account = web3.eth.account.privateKeyToAccount(PRIVATE_KEY)
receiver_address = "0xfC64329577997a2A4AE35A65044758eF23B8aC74" #адрес, который получит монеты

contract_address = "0xe9e7CEA3DedcA5984780Bafc599bD69ADd087D56" #контракт монеты, которой хотим перевести
contract = web3.eth.contract(contract_address, abi=token_abi)
print(f"My balance: {contract.functions.balanceOf(my_account.address).call()}")


#build transaction
raw_txn = {
    "from": my_account.address,
    "gasPrice": web3.eth.gasPrice,
    "gas": 200000,
    "to": contract_address,
    "value": "0x0",
    "data": contract.encodeABI('transfer', args=(receiver_address, 1128942795010)),#wei
    "nonce": web3.eth.getTransactionCount(my_account.address)
}

signed_txn = web3.eth.account.signTransaction(raw_txn, PRIVATE_KEY)
tx_hash = web3.eth.sendRawTransaction(signed_txn.rawTransaction)
#sended

print("https://bscscan.com/tx/" + (web3.toHex(tx_hash)))
